sys_script_client.script:
function onLoad() {
   var getLastRecord = new GlideAjax('LastRecord');
	getLastRecord.addParam('sysparm_name', 'getRecord');
	getLastRecord.getXML(getFields);


	function getFields(response){
		var result = response.responseXML.documentElement.getAttribute("answer");
		var rec = JSON.parse(result);
		if(rec && g_form.isNewRecord()){
			g_form.clearValue('u_first_name');
			g_form.clearValue('u_last_name');
			g_form.setValue('u_first_name', rec.first_name);
			g_form.setValue('u_last_name', rec.last_name);
		}
	}
}
