sys_script_include.script:
var LastRecord = Class.create();
LastRecord.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getRecord: function(){
		var jsonObj;
		var lr = new GlideRecord("x_299968_formcontr_table");
		lr.orderByDesc('sys_created_on');
		lr.setLimit(1);
		lr.query();	
		lr.next();
		if(lr){
			var firstName = lr.getValue("u_first_name");
			var lastName = lr.getValue("u_last_name");
			var str = {
				"first_name":firstName,
				"last_name":lastName
			};
			jsonObj = JSON.stringify(str);
		}
		return jsonObj;
	},
    type: 'LastRecord'
});
